sp_widget.client_script:
api.controller=function(spUtil, spModal) {
	var c = this;

	/*** init the login widget *******/
	spUtil.get("mfh-oob-login").then(function(response) {
		c.mfh_login = response;
	});

	/*** init variables that control templates ****/
	c.privacy1_template = true;
	c.privacy2_template = false;
	c.login_template = false;	
	/**************************************/

	/**** logic for Privacy1 template **************/
	c.userAgreementCheck = false;
	c.PIICheck = false;

	c.clickInput = function (input_name) {
		if (input_name=='UserAgreement')
			c.userAgreementCheck = !c.userAgreementCheck;
		if (input_name=='PII')
			c.PIICheck = !c.PIICheck;
		c.checkContinue();
	}
	c.checkContinue = function() {
		if (c.userAgreementCheck && c.PIICheck)
			document.querySelector('#continueButton').disabled = false;
		else
			document.querySelector('#continueButton').disabled = true;
	}

	c.showPrivacy2Template = function () {
		c.privacy1_template = false;
		c.privacy2_template = !c.privacy2_template;
	}
	/**************************************/

	/**** logic for Privacy2 template **************/

	c.showLoginTemplate = function(){
		c.privacy2_template = false;
		c.login_template = !c.login_template;
	}
	/**************************************/

};
